import React, { Component } from 'react'
import {Alert, Button, Form, FormGroup, Label, Input,FormText } from 'reactstrap';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Joi from "joi";



const schema = Joi.object().keys({
  username: Joi.string()
    .alphanum()
    .min(3)
    .max(30)
    .required(),
  password: Joi.string()
    .min(8)
    .required(),
    confirmPassword: Joi.string()
    .min(8)
    .required()
});




export default class LoginPage extends Component {

  
  constructor(props) {
    super(props);
    this.state = {
      done:false,
      signup:false,
      errorMessage: "",
      user: {
        username: "",
        password: "",
      },
    }
  }

  validuser = () => {
    if (this.state.user.password !== this.state.user.confirmPassword) {
      this.setState({ errorMessage: "Password must be match" });
      this.setState({signup:false})
      return false;
    }
    const result = Joi.validate(this.state.user, schema);
    if (result.error === null) {
      return true;
    }

    if(result.error.message.includes('username')){
      this.setState({ errorMessage: "Username invalid" });
    }
    else{
      this.setState({ errorMessage: "Password invalid" });

    }
    return false;
  };
  render() {
    return (
      <div>
        {this.state.errorMessage && (
          <Alert color="danger">{this.state.errorMessage}</Alert>
        )}
        <Form>
        <FormGroup>
          <Label for="username">Username</Label>
          <Input type="username" name="username" id="username" placeholder="Your Username" />
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Password</Label>
          <Input type="password" name="password" id="examplePassword" placeholder="Your Password " />
        </FormGroup>
        <Button>
            Login
        </Button>
        <FormText color="muted">
        Doesn't have an account? register <Link style ={{color:'white'}}to={`/register`}>here</Link>
          </FormText>
        </Form>
        </div>
    )
  }
}
