import React, { Component } from "react";
import {
  Alert,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText
} from "reactstrap";

import Joi from "joi";

const schema = Joi.object().keys({
  username: Joi.string()
    .alphanum()
    .min(3)
    .max(30)
    .required(),
  password: Joi.string()
    .min(8)
    .required(),
    confirmPassword: Joi.string()
    .min(8)
    .required()
});

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      errorMessage: "",
      user: {
        username: "",
        password: "",
        confirmPassword: ""
      },
      
    };
    this.handleChange=this.handleChange.bind(this);
    this.validuser = this.validuser.bind(this);
    this.signup = this.signup.bind(this);
  }
  handleChange = e => {
    let inputName = e.target.name;
    let inputValue = e.target.value;
    let copyUser = Object.assign({}, this.state.user);
    copyUser[inputName]=inputValue;

    this.setState({user:copyUser});
  }

  validuser = () => {
    if (this.state.user.password !== this.state.user.confirmPassword) {
      this.setState({ errorMessage: "Password must be match" });
      return false;
    }
    const result = Joi.validate(this.state.user, schema);
    if (result.error === null) {
      return true;
    }

    if(result.error.message.includes('username')){
      this.setState({ errorMessage: "Username invalid" });
    }
    else{
      this.setState({ errorMessage: "Password invalid" });

    }
    return false;
  };

  signup = event => {
    this.setState({ errorMessage: "" });
    if (this.validuser()){
      console.log('user valid')
    }
    event.preventDefault();
  };

  componentWillUpdate (){
    if (this.props.user){
      this.setState({ errorMessage: "" });

    }
  }
  render() {
    return (
      <div>
        {this.state.errorMessage && (
          <Alert color="danger">{this.state.errorMessage}</Alert>
        )}
        <Form onSubmit={this.signup}>
          <FormGroup>
            <Label for="username">Username</Label>
            <Input
              type="text"
              name="username"
              id="username"
              placeholder="Your Username"
              required
              onChange={this.handleChange}
            />
            <FormText color="white">
              Username can only container alphanumeric , with minimum 3
              characters
            </FormText>
          </FormGroup>

          <FormGroup>
            <Label for="password">Password</Label>
            <Input
              type="password"
              name="password"
              id="password"
              placeholder="Password "
              required
              onChange={this.handleChange}
            />
            <FormText color="white">
              Password must be 8 or more characters
            </FormText>
          </FormGroup>
          <FormGroup>
            <Label for="password">Re-Type Password</Label>
            <Input
              type="password"
              name="confirmPassword"
              id="confirmPassword"
              placeholder="Re-Type Password "
              required
              onChange={this.handleChange}

            />
            <FormText color="white">Re-Type your password</FormText>
          </FormGroup>
          <Button>Register</Button>
        </Form>
      </div>
    );
  }
}
